import { Blob } from "@web-std/blob";
import all from 'it-all';
import { MemoryBlockStore } from "../blockstore/memory.js";
import { pack } from "./index.js";
export async function packToBlob({ input, blockstore: userBlockstore, hasher, maxChunkSize, maxChildrenPerNode, wrapWithDirectory }) {
    const blockstore = userBlockstore ? userBlockstore : new MemoryBlockStore();
    const { root, out } = await pack({
        input,
        blockstore,
        hasher,
        maxChunkSize,
        maxChildrenPerNode,
        wrapWithDirectory
    });
    if (!userBlockstore) {
        await blockstore.close();
    }
    const carParts = await all(out);
    const car = new Blob(carParts, {
        type: 'application/car',
    });
    return { root, car };
}
